benchdata = DataFrame(
	SpeedMax 	= vcat(map(x->maximum(x), collect(values(benchdict2)))...), 
	SpeedY 		= vcat(map(x->mean(x), collect(values(benchdict2)))...), 
	SpeedMin 	= vcat(map(x->minimum(x), collect(values(benchdict2)))...), 
	Function 	= vcat(map(x-> split(x, ".")[2], collect(keys(benchdict2)))...), 
