println(pydata[:SpeedY]./cdata[:SpeedY])
println(jdata[:SpeedY]./cdata[:SpeedY])
#=
benchdata = sort(benchdata, cols = :Function)

p = plot(benchdata, x=:XLabel,y=:SpeedY, ymax=:SpeedMax, ymin=:SpeedMin, xgroup=:Function, color=:Method, 
	Scale.y_log10(minvalue=30.0),
	Guide.xlabel(""), Guide.YLabel("Time for 10<sup>7</sup> runs in ms"), 
	Geom.subplot_grid(Geom.errorbar, Geom.point),
	Theme(default_point_size=0.5mm))

